import os, requests, json

token = os.environ["GH_TOKEN"]
gists = os.environ["GIST_IDS"].split(",")
files_mapping = json.loads(os.environ["FILES_MAPPING"])

headers = {"Authorization": f"token {token}"}

for gist_id in gists:
    file_path = files_mapping.get(gist_id)
    if not file_path or not os.path.isfile(file_path):
        print(f"Skipping gist {gist_id} (file not found or not mapped)")
        continue
    with open(file_path, "r") as f:
        content = f.read()
    gist = requests.get(f"https://api.github.com/gists/{gist_id}", headers=headers).json()
    filename = list(gist["files"].keys())[0]
    payload = {"files": {filename: {"content": content}}}
    r = requests.patch(f"https://api.github.com/gists/{gist_id}", headers=headers, json=payload)
    if r.status_code == 200:
        print(f"Updated gist {gist_id} ({filename})")
    else:
        print(f"Failed to update gist {gist_id}: {r.text}")

