import ast
import os
import networkx as nx
import matplotlib.pyplot as plt

repo_dir = '.'
G = nx.DiGraph()

# Mapping of file: set(imported_files)
relationships = {}

for root, _, files in os.walk(repo_dir):
    for file in files:
        if file.endswith('.py'):
            full_path = os.path.join(root, file)
            rel_path = os.path.relpath(full_path, repo_dir)
            relationships[rel_path] = set()
            with open(full_path, encoding='utf-8') as f:
                try:
                    node = ast.parse(f.read(), filename=rel_path)
                    for n in ast.walk(node):
                        if isinstance(n, ast.Import):
                            for alias in n.names:
                                relationships[rel_path].add(alias.name.split('.')[0] + '.py')
                        elif isinstance(n, ast.ImportFrom):
                            if n.module:
                                relationships[rel_path].add(n.module.split('.')[0] + '.py')
                except Exception as e:
                    print(f'Error parsing {rel_path}: {e}')

for src, targets in relationships.items():
    G.add_node(src)
    for tgt in targets:
        if tgt in relationships:
            G.add_node(tgt)
            G.add_edge(src, tgt)

nx.write_edgelist(G, "relationships.edgelist")
print("File relationships written to relationships.edgelist")

# Optional: Create a visualization
if G.number_of_edges() > 0:
    plt.figure(figsize=(8, 6))
    pos = nx.spring_layout(G)
    nx.draw(G, pos, with_labels=True, node_size=700, font_size=8)
    plt.savefig("relationships.png")
    print("Relationship graph saved to relationships.png")

