  $signtoolPath = Get-ChildItem -Path "C:\Program Files (x86)\Windows Kits" -Filter signtool.exe -Recurse -ErrorAction SilentlyContinue | Select-Object -First 1
  if ($signtoolPath) {
    Write-Host "signtool.exe found at: $($signtoolPath.FullName)"
  } else {
    Write-Host "signtool.exe not found"
    exit 1
  }
}

