from github import Github
import os

g = Github(os.environ['GITHUB_TOKEN'])
user = g.get_user()
repos = user.get_repos()

grouped = {}
keywords = ["api", "web", "cli", "library", "tool"]

for repo in repos:
  repo_keywords = []
  for keyword in keywords:
    if keyword in repo.name.lower() or (repo.description and keyword in repo.description.lower()):
      repo_keywords.append(keyword)

  if not repo_keywords:
    repo_keywords = ["other"]

  for keyword in repo_keywords:
    if keyword not in grouped:
      grouped[keyword] = []
    grouped[keyword].append(repo.name)

for keyword, repos in grouped.items():
  print(f"{keyword.upper()}:")
  for repo in repos:
    print(f"  - {repo}")
  print()

