const owner = context.repo.owner
const repo = context.repo.repo

let mergedCount = 0
let page = 1

while (true) {
  const response = await octokit.pulls.list({
    owner,
    repo,
    state: 'all',
    per_page: 100,
    page: page
  })

  if (response.data.length === 0) break

  mergedCount += response.data.filter(pr => pr.merged_at !== null).length
  page++
}

