cat > ExecuteSQL.java << 'EOF'
import java.sql.*;
import java.nio.file.*;
import java.io.*;

public class ExecuteSQL {
    public static void main(String[] args) {
        if (args.length < 4) {
            System.out.println("Usage: java ExecuteSQL <jdbcUrl> <username> <password> <scriptPath>");
            return;
        }

        String jdbcUrl = args[0];
        String username = args[1];
        String password = args[2];
        String scriptPath = args[3];

        try (Connection conn = DriverManager.getConnection(jdbcUrl, username, password)) {
            String sql = new String(Files.readAllBytes(Paths.get(scriptPath)));
            String[] statements = sql.split(";\\s*\\n");

            for (String statement : statements) {
                if (!statement.trim().isEmpty()) {
                    try (Statement stmt = conn.createStatement()) {
                        stmt.execute(statement);
                    }
                }
            }
            System.out.println("SQL script executed successfully");
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
EOF

