const { Octokit } = require('@octokit/rest')
const octokit = new Octokit({ auth: process.env.GITHUB_TOKEN })

async function checkWriteAccess() {
  try {
    // Get collaborators with write access
    const { data: collaborators } = await octokit.repos.listCollaborators({
      owner: process.env.ORG,
      repo: process.env.REPO,
      permission: 'push'
    })

    console.log('Users with write access:')
    collaborators.forEach(collab => {
      console.log(`- ${collab.login} (${collab.permissions.push ? 'write' : 'read'})`)
    })

    // Get teams with write access
    const { data: teams } = await octokit.repos.listTeams({
      owner: process.env.ORG,
      repo: process.env.REPO,
      permission: 'push'
    })

    console.log('\nTeams with write access:')
    teams.forEach(team => {
      console.log(`- ${team.name} (${team.permission})`)
    })

  } catch (error) {
    console.error('Error:', error.message)
    process.exit(1)
  }
}

