<!DOCTYPE html>
<html>
<head>
    <title>Docker State Monitor</title>
    <style>
        body { font-family: Arial, sans-serif; margin: 40px; }
        .container { max-width: 800px; margin: 0 auto; }
        .container-state { margin: 20px 0; padding: 15px; border: 1px solid #ddd; border-radius: 5px; }
        .running { background-color: #d4edda; }
        .stopped { background-color: #f8d7da; }
        .exited { background-color: #fff3cd; }
    </style>
</head>
<body>
    <div class="container">
        <h1>Docker Container States</h1>
        <div id="container-list"></div>
    </div>

    <script>
        async function fetchDockerStates() {
            try {
                const response = await fetch('/api/containers');
                const containers = await response.json();

                const containerList = document.getElementById('container-list');
                containerList.innerHTML = '';

                containers.forEach(container => {
                    const div = document.createElement('div');
                    div.className = `container-state ${container.State}`;
                    div.innerHTML = `
                        <h3>${container.Names[0]}</h3>
                        <p><strong>ID:</strong> ${container.Id.substring(0, 12)}</p>
                        <p><strong>Status:</strong> ${container.Status}</p>
                        <p><strong>State:</strong> ${container.State}</p>
                        <p><strong>Image:</strong> ${container.Image}</p>
                    `;
                    containerList.appendChild(div);
                });
            } catch (error) {
                console.error('Error fetching container states:', error);
            }
        }

        // Fetch initially and every 30 seconds
        fetchDockerStates();
        setInterval(fetchDockerStates, 30000);
    </script>
</body>
</html>
EOF

cat > server.js << 'EOF'
const express = require('express');
const { exec } = require('child_process');
const app = express();
const port = 3000;

app.use(express.static('.'));

app.get('/api/containers', (req, res) => {
    exec('docker ps -a --format "{{json .}}"', (error, stdout) => {
        if (error) {
            return res.status(500).json({ error: error.message });
        }

        const containers = stdout.trim().split('\n')
            .filter(line => line.trim())
            .map(line => JSON.parse(line));

        res.json(containers);
    });
});

app.listen(port, () => {
    console.log(`Server running at http://localhost:${port}`);
});
EOF

