import javaposse.jobdsl.dsl.DslFactory
import jenkins.model.Jenkins
import jenkins.branch.BranchBuildStrategy
import jenkins.branch.DefaultBranchPropertyStrategy
import jenkins.branch.NoTriggerBranchProperty

Jenkins jenkins = Jenkins.instance

// Create branch build strategy
BranchBuildStrategy strategy = new jenkins.branch.ChangeRequestBranchBuildStrategy()

// Configure branch property strategy
def branchPropertyStrategy = new DefaultBranchPropertyStrategy(
    new NoTriggerBranchProperty()
)

// Apply configuration to all multibranch projects
jenkins.getAllItems(com.cloudbees.hudson.plugins.folder.Folder.class).each { folder ->
    folder.getItems().each { item ->
        if (item instanceof org.jenkinsci.plugins.workflow.multibranch.WorkflowMultiBranchProject) {
            item.setBuildStrategies([strategy])
            item.setBranchPropertyStrategy(branchPropertyStrategy)
            item.save()
        }
    }
}
EOF

