const { Octokit } = require('@octokit/rest')
const octokit = new Octokit({ auth: process.env.GITHUB_TOKEN })

// Get source project board data
const project = await octokit.projects.get({
  project_id: ${{ github.event.project.id }}
})

// Create new project in target repo
const newProject = await octokit.projects.createForRepo({
  owner: process.env.TARGET_OWNER,
  repo: process.env.TARGET_REPO,
  name: project.data.name,
  body: project.data.body
})

// Get source project columns
const columns = await octokit.projects.listColumns({
  project_id: ${{ github.event.project.id }}
})

// Recreate columns in target project
for (const column of columns.data) {
  const newColumn = await octokit.projects.createColumn({
    project_id: newProject.data.id,
    name: column.name
  })

  // Get cards from source column
  const cards = await octokit.projects.listCards({
    column_id: column.id
  })

  // Recreate cards in target column
  for (const card of cards.data) {
    await octokit.projects.createCard({
      column_id: newColumn.data.id,
      note: card.note,
      content_id: card.content_id,
      content_type: card.content_type
    })
  }
}

