const axios = require('axios');

async function syncStats() {
  const headers = {
    Authorization: `Bearer ${process.env.GITHUB_TOKEN}`,
    Accept: 'application/vnd.github.v3+json'
  };

  // Get original repo stats
  const original = await axios.get(
    `https://api.github.com/repos/${process.env.ORIGINAL_REPO}`,
    { headers }
  );

  // Update fork description to match stars and watchers
  await axios.patch(
    `https://api.github.com/repos/${github.repository}`,
    {
      description: `Fork with ${original.data.stargazers_count} stars and ${original.data.watchers_count} watchers`
    },
    { headers }
  );
}

syncStats().catch(error => {
  console.error('Error:', error.response?.data || error.message);
