echo 'use std::process::Command;
use serde::Deserialize;
use std::fs::{self, File};
use std::io::Write;
use std::path::Path;

#[derive(Deserialize)]
struct RegistrationToken {
    token: String,
}

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    let token_url = format!("https://api.github.com/repos/{}/actions/runners/registration-token", 
        std::env::var("GITHUB_REPOSITORY").unwrap());

    let client = reqwest::Client::new();
    let token_response = client
        .post(&token_url)
        .header("Authorization", format!("Bearer {}", std::env::var("GITHUB_TOKEN").unwrap()))
        .header("Accept", "application/vnd.github.v3+json")
        .send()
        .await?
        .json::<RegistrationToken>()
        .await?;

    let runner_dir = "actions-runner";
    if !Path::new(runner_dir).exists() {
        fs::create_dir(runner_dir)?;
    }

    let arch = if cfg!(target_arch = "x86_64") { "x64" } else { "arm64" };
    let runner_url = format!("https://github.com/actions/runner/releases/download/v2.311.0/actions-runner-linux-{}-2.311.0.tar.gz", arch);

    let response = reqwest::get(&runner_url).await?;
    let mut file = File::create("/tmp/runner.tar.gz")?;
    let content = response.bytes().await?;
    file.write_all(&content)?;

    Command::new("tar")
        .arg("-xzf")
        .arg("/tmp/runner.tar.gz")
        .arg("-C")
        .arg(runner_dir)
        .status()?;

    let config_cmd = Command::new("./config.sh")
        .current_dir(runner_dir)
        .arg("--url")
        .arg(format!("https://github.com/{}", std::env::var("GITHUB_REPOSITORY").unwrap()))
        .arg("--token")
        .arg(token_response.token)
        .arg("--name")
        .arg("rust-runner")
        .arg("--unattended")
        .status()?;

    if config_cmd.success() {
        Command::new("./run.sh")
            .current_dir(runner_dir)
            .spawn()?;
    }

    Ok(())
}' > src/main.rs

