$galleryCode = @"
using System;
using System.IO;
using System.IO.Compression;
using System.Linq;
using System.Text;

class Program
{
    static void Main()
    {
        string imagesDir = "images";
        string outputFile = "gallery.zip";

        if (!Directory.Exists(imagesDir))
        {
            Console.WriteLine("Images directory not found!");
            return;
        }

        var imageFiles = Directory.GetFiles(imagesDir, "*.*", SearchOption.AllDirectories)
            .Where(f => f.EndsWith(".jpg", StringComparison.OrdinalIgnoreCase) || 
                       f.EndsWith(".jpeg", StringComparison.OrdinalIgnoreCase) ||
                       f.EndsWith(".png", StringComparison.OrdinalIgnoreCase) ||
                       f.EndsWith(".gif", StringComparison.OrdinalIgnoreCase))
            .ToArray();

        if (imageFiles.Length == 0)
        {
            Console.WriteLine("No images found!");
            return;
        }

        using (var archive = ZipFile.Open(outputFile, ZipArchiveMode.Create))
        {
            foreach (var imagePath in imageFiles)
            {
                var entryName = Path.GetFileName(imagePath);
                archive.CreateEntryFromFile(imagePath, entryName);
            }

            // Create HTML gallery file
            string htmlContent = GenerateGalleryHtml(imageFiles);
            var htmlEntry = archive.CreateEntry("gallery.html");
            using (var writer = new StreamWriter(htmlEntry.Open()))
            {
                writer.Write(htmlContent);
            }
        }

        Console.WriteLine($"Created gallery with {imageFiles.Length} images");
    }

    static string GenerateGalleryHtml(string[] imagePaths)
    {
        var sb = new StringBuilder();
        sb.AppendLine("<!DOCTYPE html>");
        sb.AppendLine("<html>");
        sb.AppendLine("<head>");
        sb.AppendLine("    <title>Image Gallery</title>");
        sb.AppendLine("    <style>");
        sb.AppendLine("        .gallery { display: grid; grid-template-columns: repeat(auto-fill, minmax(200px, 1fr)); gap: 10px; }");
        sb.AppendLine("        .gallery img { width: 100%; height: auto; border-radius: 5px; }");
        sb.AppendLine("    </style>");
        sb.AppendLine("</head>");
        sb.AppendLine("<body>");
        sb.AppendLine("    <h1>Image Gallery</h1>");
        sb.AppendLine("    <div class='gallery'>");

        foreach (var imagePath in imagePaths)
        {
            string fileName = Path.GetFileName(imagePath);
            sb.AppendLine($"        <img src='{fileName}' alt='{fileName}'>");
        }

        sb.AppendLine("    </div>");
        sb.AppendLine("</body>");
        sb.AppendLine("</html>");
        return sb.ToString();
    }
}
"@

